// src/App.js

import React, { useState } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'react-flow-renderer';
import CustomNode from './CustomNode';

import './App.css';

const nodeTypes = {
  textMessage: CustomNode,
};

const initialNodes = [
  { id: '1', type: 'textMessage', data: { label: 'Text Message 1' }, position: { x: 250, y: 5 } },
];

const initialEdges = [];

let id = 2; // Incremental ID for new nodes

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);

  const onConnect = (params) => setEdges((eds) => addEdge(params, eds));
  const onElementClick = (event, element) => setSelectedNode(element);

  const addNode = () => {
    const newNode = {
      id: id.toString(),
      type: 'textMessage',
      data: { label: `Text Message ${id}` },
      position: { x: Math.random() * 400, y: Math.random() * 400 },
    };
    id++;
    setNodes((nds) => [...nds, newNode]);
  };

  const validateFlow = () => {
    let isValid = true;
    nodes.forEach((node) => {
      const nodeEdges = edges.filter((edge) => edge.source === node.id || edge.target === node.id);
      if (node.type === 'textMessage' && nodeEdges.length === 0) {
        isValid = false;
      }
    });
    if (!isValid) {
      alert('Validation failed: Some nodes do not have target handles.');
    } else {
      console.log('Flow is valid. Saving flow...', { nodes, edges });
      // Save the flow logic here
    }
  };

  return (
    <div style={{ height: '100vh' }}>
      <ReactFlowProvider>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onElementClick={onElementClick}
          nodeTypes={nodeTypes}
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>
        {selectedNode && (
          <div className="settings-panel">
            <h3>Node Settings</h3>
            <p>Label: {selectedNode.data.label}</p>
          </div>
        )}
        <div >
          <button onClick={addNode}>Add Node</button>
          <button onClick={validateFlow}>Save</button>
        </div>
      </ReactFlowProvider>
    </div>
  );
}

export default App;
