import React, { useState, useEffect } from 'react';
import { Handle } from 'react-flow-renderer';
import Button from '@mui/material/Button';

const CustomNode = ({ data, isSelected, onLabelChange }) => {
  return (
    <div>
      <Button
        style={{
          width: '16rem',
          border: isSelected ? 'solid 2px blue' : 'solid 0.5px black',
          height: '4rem',
        }}
      >
        <Handle onClick={onLabelChange} type="target" position="right" />
        {data.label}
        <Handle type="source" position="left" />
      </Button>
    </div>
  );
};

export default CustomNode;
